INSERT INTO `msc_todo` (`headline`, `byline`, `article`, `status`, `date`, `id`) VALUES
('CSC801 - Current topics in Computer Systems', 'by Dr. F. Chete', '<ol class=\"alpha-list\">\r\n<li>Select a current topic in Computer Systems after the year 2016.</li>\r\n<li>Write a literature review on the selected topic.</li>\r\n<li>Highlight real world applications, limitations and recommendations of the selected topic.</li>\r\n</ol>\r\n<p></p>\r\n<b>Submission date:</b> As soon as First Semester Examination time-table is published. <br/>\r\n<b>Submission format:</b> Literture Review (typed).\r\n<p></p>\r\n<a href=\"blog.php?20190401090000\" target=\"_blank\">View list of research topics and assigned students.</a>', 0, '2019-04-08 08:00:00', 1),
('CSC841 - Programming Languages', 'by Dr. K.C. Ukaoha', 'Get the complete description of the Backus-Naur Form (BNF Notation)\r\n<p></p>\r\n<b>Submission date:</b> Thursday, April 18, 2019. <br/>\r\n<b>Submission format:</b> Hand-written or Typed. \r\n<p></p>\r\n<a href=\"blog.php?20190414180000\" target=\"_blank\">[RESOURCE] Understanding The BNF Notation (Backus-Naur Form) by Mr. Tugbeh, E.O.</a>', 0, '2019-04-11 11:00:00', 2),
('CSC801 - Current topics in Computer Systems', 'by Dr. F. Chete', '<ol class=\"alpha-list\">\r\n<li>What is the status and nature of E-Government in your state of origin?</li>\r\n<li>What are the challenges and barriers to E-Government implementation in your state of origin, and what is the way forward?</li>\r\n<li>What are the current E-Government initiatives in your state of origin?</li>\r\n<li>What is the framework readiness of E-Government in your state of origin with respect to the following models;\r\n<ol class=\"roman-list\">    \r\n<li>Government to Government (G2G)</li>\r\n<li>Government to Citizens (G2C)</li>\r\n<li>Government to Business (G2B)</li>\r\n</ol>\r\n</li>\r\n</ol>\r\n<p></p>\r\n<b>Submission date:</b> As soon as First Semester Examination time-table is published. <br/>\r\n<b>Submission format:</b> Literture Review (typed).', 0, '2019-04-15 08:00:00', 3),
('CSC803 - Advanced Compiler Construction', 'by Dr. F.I. Amadin', 'Write the Syntax Rule for generating code in Repeat-Until and For-Loop statements respectively.\r\n<p></p>\r\n<b>Submission date:</b> Monday, April 22, 2019. <br/>\r\n<b>Submission format:</b> Hand-written.', 0, '2019-04-15 11:00:00', 4),
('CSC824 - System Analysis & Design', 'by Prof. (Mrs) F. Egbokhare', '<ol class=\"alpha-list\">\r\n<li>Define System Environment in relation to the System Boundary.</li>\r\n<li>State 20 Facts Finding Techniques and write short notes on each.</li>\r\n<li>Get 3 journals/articles on <ins>\"Problems of lack of user involvement in information systems development projects\"</ins>. Cite your sources properly and include <i>References</i> at the end of your report.</li>\r\n<p></p>\r\n<b>Submission date:</b> Tuesday, April 23, 2019. <br/>\r\n<b>Submission format:</b> Hand-written or Typed.\r\n<p></p>\r\n<a href=\"media/uploads/downloads/20190423170000.pdf\" target=\"_blank\">[DOWNLOAD] 20 Facts Finding Techniques.</a>\r\n', 0, '2019-04-16 08:00:00', 5),
('CSC834 - Design and Analysis of Algorithm', 'by Prof. A.A. Imianvan', '<ol class=\"alpha-list\">\r\n<li>Illustrate the Game of Life algorithm using 2 iterations (i.e. 4 generations). </li>\r\n<li>Obtain an algorithm to solve the Fibonacci Sequence for the first 250 terms.</li>\r\n</ol>\r\n<p></p>\r\n<b>Submission date:</b> Thursday, April 25, 2019. <br/>\r\n<b>Submission format:</b> Hand-written in assignment book.\r\n\r\n<p></p>\r\n<a href=\"media/uploads/downloads/20190425090000.pdf\" target=\"_blank\">[DOWNLOAD] Understanding The Game of Life Algorithm by Ms. Oboro Oizamisi F.</a>', 0, '2019-04-18 08:00:00', 6),
('CSC824 - System Analysis &amp; Design', 'by Prof. (Mrs) F. Egbokhare', '<b>*** Group Assignment ***</b>\r\n<ol class=\"alpha-list\">\r\n<li>Identify an existing or proposed System at your place of work;</li>\r\n<li>Study the system and model it as a Business Process.</li>\r\n<li>Draw a <a href=\"https://www.brighthubpm.com/project-planning/12712-connecting-a-projects-parts-with-a-network-diagram/\" target=\"_blank\">Network Diagram</a> to show the activities of the project, indicating time concurrences and dependencies where applicable.</li>\r\n</ol>\r\n<p></p>\r\n<b>Submission date:</b> Tuesday, April 30, 2019. <br/>\r\n<b>Submission format:</b> Typed. <br/>\r\n<b>Presentation format:</b> Group Presentation.\r\n<p></p>\r\n<a href=\"blog.php?20190423090000\" target=\"_blank\">[RESOURCE] Lecture note on Business Process Modelling.</a>', 0, '2019-04-23 08:00:00', 7),
('CSC801 - Current topics in Computer Systems', 'by Dr. F. Chete', '<ol class=\"alpha-list\">\r\n<li>\r\nCompare and contrast features of the top 5 programming languages; \r\n<ol class=\"roman-list\">\r\n<li>C++</li>\r\n<li>Java</li>\r\n<li>Python</li>\r\n<li>R</li>\r\n<li>C# or JavaScript</li>\r\n</ol>\r\n</li>\r\n<li>Use the Data Science Life Cycle to predict the reoccurrence of Cancer and Asthma, and take appropriate measures to prevent them.</li>\r\n</ol>\r\n<p></p>\r\n<b>Submission date:</b> As soon as First Semester Examination time-table is published. <br/>\r\n<b>Submission format:</b> Typed.', 0, '2019-04-29 08:00:00', 8),
('CSC824 - System Analysis &amp; Design', 'by Prof. (Mrs) F. Egbokhare', '<b>*** Group Assignment ***</b> <br/>\r\nWrite a report on the following cost estimation models;\r\n<ol class=\"roman-list\">\r\n<li>Line of Code (All groups).</li>\r\n<li>Cocomo 1 &amp; 2 (Group 1).</li>\r\n<li>Function Point (Group 2).</li>\r\n<li>Putman (Group 3).</li>\r\n<li>Story Point and Analogy-based Estimation (Group 4).</li>\r\n<li>Planning Poker (Group 5).</li>\r\n</ol>\r\n<p></p>\r\n<b>Submission date:</b> Tuesday, May 7, 2019. <br/>\r\n<b>Submission format:</b> Typed. <br/>\r\n<b>Presentation format:</b> Group Presentation.\r\n', 0, '2019-04-30 08:00:00', 9),
('CSC839 - Seminar', 'by Dr. S.S. Daodu', 'Write a literature review on the 2 materials given to you by Dr. S.S. Daodu, H.O.D of Computer Science, University of Benin 2018/2019. \r\n\r\n<p><p>\r\nContact the Course Rep, Mr. Tugbeh (<a href=\"tel:+2348169960927\">08169960927</a>) to get your materials if you have not been given.\r\n<p></p>\r\n<b>Submission date:</b> Tuesday, May 7, 2019. <br/>\r\n<b>Submission format:</b> Typed.', 0, '2019-04-30 11:00:00', 10),
('CSC834 - Design and Analysis of Algorithm', 'by Prof. A.A. Imianvan', 'Show the walkthrough for Tower of Hanoi from 3 discs to 25 discs.\r\n<p></p>\r\n<b>Submission date:</b> Thursday, May 9, 2019. <br/>\r\n<b>Submission format:</b> Hand-written in assignment book.', 0, '2019-05-02 08:00:00', 11),
('CSC803 - Advanced Compiler Construction', 'by Dr. F.I. Amadin', 'Write out the Three Address Code (TAC) for the program below;<br/>\r\n<code>\r\n<pre>\r\nbegin \r\n    prod := 0; \r\n    i = 1; \r\n    do begin \r\n        prod := product + a[i] * b[i]; \r\n        i = i + 1; \r\n    end \r\n    while i <= 20 \r\nend \r\n</pre>\r\n</code>\r\n<p></p>\r\n<b>Submission date:</b> Monday, May 13, 2019. <br/>\r\n<b>Submission format:</b> Hand-written.', 0, '2019-05-06 11:00:00', 12),
('CSC824 - System Analysis &amp; Design', 'by Prof. (Mrs) F. Egbokhare', '<ol class=\"alpha-list\">\r\n<li>Define <ins>Crow Feet Notation</ins> and its application in an Entity Relationship Diagram.</li>\r\n<li>\r\n<b>*** Group Assignment ***</b>\r\n<ol class=\"roman-list\">\r\n<li>Use the <ins>Cost Estimation Model</ins> assigned to your group to determine the cost of the proposed system i.e. the business case you want to implement in the organisation. \r\n<b>NOTE:</b> Group 5 should use Planning Poker cost estimation model.</li>\r\n<li>Represent your proposed system using an <ins>Entity Relationship Diagram</ins>. Show the various; Entities, Attributes, and Relationships.</li>\r\n<li>Represent your proposed system using an <ins>Object Oriented Database Model</ins> and show the various tables and fields. You can make use of Microsoft Access.</li>\r\n</ol>\r\n</li>\r\n</ol>\r\n<p></p>\r\n<b>Submission date:</b> Tuesday, May 14, 2019. <br/>\r\n<b>Submission format:</b> Typed. <br/>\r\n<b>Presentation format:</b> Group Presentation.\r\n<p></p>\r\n<a href=\"http://www2.cs.uregina.ca/~bernatja/crowsfoot.html\" target=\"_blank\">[RESOURCE] Crow\'s Foot Notation</a>', 0, '2019-05-07 08:00:00', 13),
('CSC834 - Design and Analysis of Algorithm', 'by Prof. A.A. Imianvan', 'Get any UNIBEN past question on Design and Analysis of Algorithm, and answer the question on Genetic Algorithms.\r\n<p></p>\r\n<b>Submission date:</b> Thursday, May 16, 2019. <br/>\r\n<b>Submission format:</b> Hand-written in assignment book.', 0, '2019-05-09 08:00:00', 14),
('CSC803 - Advanced Compiler Construction', 'by Dr. F.I. Amadin', 'Find the cost of the program below;<br/>\r\n<code>\r\n<pre>\r\n1	prod := 0 \r\n2	i 	:= 1\r\n3	t1 	:= i * 4\r\n4	t2 	:= a[t1]\r\n5	t3 	:= i * 4\r\n6	t4 	:= b[t3]\r\n7	t5	:= t2 * t4\r\n8	t6 	:= prod + t5\r\n9	prod := t6\r\n10	t7	:= i + 1\r\n11	i	:= t7\r\n12	if (i <= 20) GOTO 3\r\n</pre>\r\n</code>\r\n<p></p>\r\n<b>HINT:</b> You can only find the cost of an Assembly code when you know the Registers and Memory Allocation.\r\n<p></p>\r\n<b>Submission date:</b> Monday, May 20, 2019. <br/>\r\n<b>Submission format:</b> Hand-written.', 0, '2019-05-13 11:00:00', 15),
('CSC824 - System Analysis &amp; Design', 'by Prof. (Mrs) F. Egbokhare', '<b>*** Group Assignment ***</b>\r\n<ol class=\"alpha-list\">\r\n<li>Draw the <ins>Unified Modeling Language (UML)</ins> and <ins>User-Case</ins> diagram of the proposed system your group wants to implement in the organisation.<p></p></li>\r\n<li>As a group, outline the factors that influenced the following decisions;\r\n<ol class=\"roman-list\">\r\n<li>User Involvement</li>\r\n<li>Programming Language(s)</li>\r\n<li>Hardware Environment</li>\r\n<li>Test Data</li>\r\n<li>Training and Documentation</li>\r\n<li>Changeover Method</li>\r\n</ol>\r\n</li>\r\n<li>As a group leader, document the following;\r\n<ol class=\"roman-list\">\r\n<li>When, where and how often meetings were held.</li>\r\n<li>What channels were used for the meetings.</li>\r\n<li>Issues that came up during each meeting.</li>\r\n<li>How were the issues resolved.</li>\r\n<li>How did you deal with uncoperative group members.</li>\r\n</ol>\r\n</li>\r\n</ol>\r\n<p></p>\r\n<b>Submission date:</b> Tuesday, June 11, 2019. <br/>\r\n<b>Submission format:</b> Typed. <br/>\r\n<b>Presentation format:</b> Group Presentation.\r\n<p></p>\r\n[RESOURCE] <a href=\"https://en.m.wikipedia.org/wiki/Unified_Modeling_Language\" target=\"_blank\">Unified Modeling Language (UML)</a>, \r\n<a href=\"https://en.m.wikipedia.org/wiki/Use_case_diagram\" target=\"_blank\">Use-Case Diagram</a>', 0, '2019-05-14 08:00:00', 16),
('CSC834 - Design and Analysis of Algorithm', 'by Prof. A.A. Imianvan', 'State 20 problems solved with the application of Genetic Algorithms. Write the title, author, year it was published and website address.\r\n<p></p>\r\n<b>Submission date:</b> Thursday, May 23, 2019. <br/>\r\n<b>Submission format:</b> Hand-written in assignment book.', 0, '2019-05-16 08:00:00', 17),
('CSC801 - Current topics in Computer Systems', 'by Dr. F. Chete', 'Discuss other areas of IoT applications in Education and Health sector.\r\n<p></p>\r\n<b>Submission date:</b> Monday, June 24, 2019. <br/>\r\n<b>Submission format:</b> Typed.\r\n<p></p>\r\n<a href=\"https://dzone.com/articles/top-10-uses-of-the-internet-of-things\" target=\"_blank\">[RESOURCE] Application Areas of Internet of Things</a>', 0, '2019-06-03 08:00:00', 18),
('CSC834 - Design and Analysis of Algorithm', 'by Prof. A.A. Imianvan', 'State the following Meta-Heuristics algorithms and give 5 real world applications of each;\r\n<ul class=\"roman-list\">\r\n<li>Mosquito Swarm Algorithm (MSA)</li>\r\n<li>Particle Swarm Optimization Algorithm</li>\r\n<li>Rats Herds Algorithm (RATHA)</li>\r\n<li>Fish School Algorithm</li>\r\n<li>Millipede Algorithm</li>\r\n<li>Bumble Bee Swarm Algorithm</li>\r\n<li>Scorpion Algorithm</li>\r\n<li>Cockroach Algorithm</li>\r\n<li>Sex Algorithm</li>\r\n<li>Lion Optimization Algorithm</li>\r\n<li>Zooplankton Swarm Algorithm (ZSA)</li>\r\n<li>Bat Algorithm</li>\r\n<li>Feral-Dogs Herds Algorithm (FDHA)</li>\r\n<li>Ant Colony Algorithm</li>\r\n<li>Rain Drop Algorithm</li>\r\n<li>Monkey Search Algorithm</li>\r\n<li>Wolf Pack Search Algorithm</li>\r\n<li>Shuffled Frog Leaping Algorithm</li>\r\n<li>Spider Algorithm</li>\r\n<li>Fire Fly Algorithm</li>\r\n</ul>\r\n<b>NOTE:</b> Meta-Heuristics Algorithms are also referred to as Nature-Inspired or Bio-Inspired Algorithms. Kindly refer to Course Material <b>111-481-1-PB</b>.\r\n<p></p>\r\n<a href=\"media/uploads/downloads/20190606090000.pdf\" target=\"_blank\">[RESOURCE] Meta-Heuristics Algorithms based on the Grouping of Animals by Social Behavior for the Traveling Salesman Problem</a>\r\n<p></p>\r\n<b>Submission date:</b> Thursday, June 13, 2019. <br/>\r\n<b>Submission format:</b> Hand-written in assignment book.', 0, '2019-06-06 08:00:00', 19),
